if (document.querySelector(".header__button")) {
    document
        .querySelector(".header__button")
        .addEventListener("click", function (e) {
            // document.querySelector(".header__button").classList.toggle("active");
            // document.querySelector(".header__catalog").classList.toggle("active");

            var newSvg = document.createElementNS(
                "http://www.w3.org/2000/svg",
                "svg"
            );
            newSvg.classList.add("icon-box");

            // Создаем элемент "use" внутри SVG
            var useElement = document.createElementNS(
                "http://www.w3.org/2000/svg",
                "use"
            );

            if (document.querySelector(".header__button.active")) {
                useElement.setAttribute("href", "img/icons/icons.svg#Vector");
                document
                    .querySelector(".header__button")
                    .classList.remove("active");
                document
                    .querySelector(".header__catalog")
                    .classList.remove("active");
            } else {
                useElement.setAttribute("href", "img/icons/icons.svg#arrow");
                document
                    .querySelector(".header__button")
                    .classList.add("active");
                document
                    .querySelector(".header__catalog")
                    .classList.add("active");
            }

            // Добавляем "use" внутрь SVG
            newSvg.appendChild(useElement);

            // Находим элемент, который вы хотите заменить
            var targetElement = document.querySelector(".header__button svg");

            // Заменяем элемент новым SVG-фрагментом
            targetElement.parentNode.replaceChild(newSvg, targetElement);
        });
}

